import styled from 'styled-components';

export const About = styled.div`
    min-height: 90vh;
    display: flex;
    align-items:center;
    justify-content: space-between;
    padding: 5rem 10rem;
    color: white;
    position: absolute;
    top: 10%;
`;
export const Description = styled.div`
flex: 1;
padding-right: 5rem;
h2 {
    font-weight: lighter;
}
`;
export const Image = styled.div`
    flex: 1; 
    overflow: hidden;
    img{
        width: 100%;
        height: 80vh;
        object-fit:cover;
    }
`;
export const Hide= styled.div`
    overflow:hidden;

`;
